mapper.select:
    select 
    id, goodsid, comment, DATE_FORMAT(create_time,"%Y-%m-%d %T") as create_time, score, userid, ishidden,(select  AVG(b.score) from ns_comment b
    	where b.flag=1 and a.goodsid=b.goodsid
    )as avgscore,
    (select b.username from ns_user b where b.flag=1 and b.userid=a.userid) as username,
    (select b.gname from ns_goods b where b.id=a.goodsid) as gname,
    (select b.goodimg from ns_goods b where b.id=a.goodsid) as gimg,
    a.orderid 
    from ns_comment a where flag = 1
	    	
	    	 	and goodsid=${map.goodsid}
	    	
	    	
	    	
	    		and userid=${map.userid}
	    	
	    	
	    	
	    		and orderid=${map.orderid}

    select 
    id, goodsid, comment, DATE_FORMAT(create_time,"%Y-%m-%d %T") as create_time, score, userid, ishidden,(select  AVG(b.score) from ns_comment b
    	where b.flag=1 and a.goodsid=b.goodsid
    )as avgscore,
    (select b.username from ns_user b where b.flag=1 and b.userid=a.userid) as username,
    (select b.gname from ns_goods b where b.id=a.goodsid) as gname,
    (select b.goodimg from ns_goods b where b.id=a.goodsid) as gimg,
    a.orderid 
    from ns_comment a where flag = 1
	    	
	    	 	and goodsid=${map.goodsid}
	    	
	    	
	    	
	    		and userid=${map.userid}
	    	
	    	
	    	
	    		and orderid=${map.orderid}
